mapper.select:
  		select 
	  	
	  	from
		(select  from ulewo_topic c inner join ulewo_user_friend f on c.userid = f.friend_userid 
		inner join ulewo_user u on u.userid = f.friend_userid
		where f.userid = #{param.userId,jdbcType=VARCHAR}
		UNION 
		select   from ulewo_blog c inner join ulewo_user_friend f on c.userid = f.friend_userid 
		inner join ulewo_user u on u.userid = f.friend_userid
		where f.userid= #{param.userId,jdbcType=VARCHAR}
		UNION 
		select  from ulewo_blast c inner join ulewo_user_friend f on c.userid = f.friend_userid 
		inner join ulewo_user u on u.userid = f.friend_userid
		where f.userid= #{param.userId,jdbcType=VARCHAR}
		UNION 
		select  from ulewo_group c inner join ulewo_user_friend f on c.group_userid = f.friend_userid 
		inner join ulewo_user u on u.userid = f.friend_userid
		where f.userid= #{param.userId,jdbcType=VARCHAR}
		) t  
		ORDER BY t.create_time desc limit #{page.start},#{page.end}

  		select 
	  	
	  	from
		(select  from ulewo_topic c  
		inner join ulewo_user u on u.userid = c.userid
		where u.userid=#{param.userId,jdbcType=VARCHAR}
		UNION 
		select  from ulewo_blog c 
		inner join ulewo_user u on u.userid = c.userid
		where u.userid=#{param.userId,jdbcType=VARCHAR}
		UNION 
		select  from ulewo_blast c 
		inner join ulewo_user u on u.userid = c.userid
		where u.userid=#{param.userId,jdbcType=VARCHAR}
		UNION 
		select  from ulewo_group c 
		inner join ulewo_user u on u.userid = c.group_userid
		where u.userid=#{param.userId,jdbcType=VARCHAR}
		) t  
		ORDER BY t.create_time desc limit #{page.start},#{page.end}

select count(1) allCount from ulewo_topic c  
inner join ulewo_user u on u.userid = c.userid
where u.userid=#{param.userId,jdbcType=VARCHAR}

  		select  from ulewo_topic c  
		inner join ulewo_user u on u.userid = c.userid
		where u.userid=#{param.userId,jdbcType=VARCHAR}
		ORDER BY c.create_time desc limit #{page.start},#{page.end}

