mapper.select:
select
from ulewo_group where 1=1
order by create_time desc limit #{page.start},#{page.end}

select
,(select count(1) from ulewo_group_member u where u.gid = g.gid and u.member_type = 'Y') members ,(select count(1) from ulewo_topic t where t.gid = g.gid) topicCount ,u.username authorName from ulewo_group g inner join ulewo_user u on u.userid=g.group_userid where 1 = 1
order by ${param.orderBy}

select
,(select count(1) from ulewo_group_member u where u.gid = g.gid and u.member_type = 'Y') members ,(select count(1) from ulewo_topic t where t.gid = g.gid) topicCount ,(select name from ulewo_group_category c where c.group_Category_Id = g.p_categroy_id) pCategoryName ,(select name from ulewo_group_category c where c.group_Category_Id = g.categroy_id) categoryName ,u.username authorName from ulewo_group g inner join ulewo_user u on u.userid=g.group_userid where 1=1
order by ${param.sort} ${param.order}
order by ${param.orderBy}
limit #{page.start},#{page.end}
