mapper.select:
select
,(select count(1) from ulewo_topic_comment tc where tc.topic_id = t.topic_id and EXISTS(select 1 from ulewo_user u where u.userid=tc.userid)) commentCount ,u.username userName,u.user_icon userIcon ,c.name categoryName from ulewo_topic t inner join ulewo_user u on u.userid = t.userid left join ulewo_topic_category c on c.category_id = t.category_id where 1=1
order by ${param.orderBy}
order by grade desc,last_comment_time desc ,create_time desc
limit #{page.start},#{page.end}

select 

from ulewo_topic t
where topic_id = #{topicId,jdbcType=INTEGER}
